mapper.select:
select username, nickname, id, u_address, email, birth_date, gender, diary_private, u_tel
from member
where u_no = #{uNo}

mapper.update:
update member set 
	nickname = #{nickName}, email = #{email}, id = #{id}, u_tel = #{uTel}, u_address = #{uAddress},
	id = #{id}, gender = #{gender}, diary_private = #{diaryPrivate}
where u_no = #{uNo}

update member set
	password = #{password}
where u_no = #{uNo}
