mapper.select:
select id
as userId, username as userName, user_type as userType from member
where email = #{email}

mapper.update:
update member set
nickname = #{nickName}, email = #{email}, id = #{id}, u_tel = #{uTel},
u_address = #{uAddress},
diary_private = #{diaryPrivate}

	, file_no = #{fileNo}

where u_no = #{uNo}

update member set
password = #{password}
where u_no = #{uNo}

update member
set file_no = #{fileNo}
where u_no = #{uNo}

		update member set 
			nickname = #{nickName}, email = #{email}, u_tel = #{uTel}, u_address = #{uAddress},
			 diary_private = #{diaryPrivate} 
        where u_no = #{uNo}

mapper.select:
select *
from member
join file
using (file_no)
where u_no=#{uNo} or u_no=#{mNo}
